const categoryTitle = computed({
  get: () => category.value.title,
  set: (value) => {
    const node = categories.find((item) => item.title === value);
    if (node) {
      const bottomLink = navBottomLink(node);
      navigateTo({ path: bottomLink });
    } else {
      navigateTo({ path: '/' });
    }
  },
});
